layout(layoutConstraints.maxWidth, layoutConstraints.maxHeight) {
   placeables.forEachIndexed { index, placeable ->

       val position = state.getPositionFor(index)
       val scale = state.getScaleFor(position)
       placeable.placeWithLayer(
           position = position,
           layerBlock = {
               this.scaleX = scale
               this.scaleY = scale
           }
       )
   }
}
–ü–æ–ª—É—á–∞–µ–º —Å–µ—Ç–∫—É —Å –ø—Ä–∞–≤–∏–ª—å–Ω—ã–º —Å–¥–≤–∏–≥–æ–º —Ä—è–¥–æ–≤ –∏ —É–∂–µ —Ä–∞—Å—Å—á–∏—Ç–∞–Ω–Ω—ã–º scale —ç–ª–µ–º–µ–Ω—Ç–æ–≤. 
–û—Å—Ç–∞–ª–æ—Å—å —Å–∞–º–æ–µ –∏–Ω—Ç–µ—Ä–µ—Å–Ω–æ–µ: –æ–±—Ä–∞–±–æ—Ç–∞—Ç—å drag-–∂–µ—Å—Ç, —á—Ç–æ–±—ã –¥–≤–∏–≥–∞—Ç—å –∫–æ–Ω—Ç–µ–Ω—Ç –≤–Ω—É—Ç—Ä–∏ layout. 
