        NSLog(@"%s is %lu characters long", str, strlen(str));
    }
    fclose(strsFile);
    return 0;
} int main(int argc, const char * argv[]) {
    if (argc == 1) {
        NSLog(@"you need to provide a file name");
        return 1;
    }
    FILE *strsFile = fopen(argv[1], "r");
    char str[100];
    while (fgets(str, 100, strsFile)) {
        str[strlen(str) - 1] = '\0';
        NSLog(@"%s is %lu characters long", str, strlen(str));
    }
    fclose(strsFile);
    return 0;
